(ns advent-calendar.day-1)

(defn parenthesis-to-int
  "Turn ( to 1 and ) to -1"
  [parenthesis]
  (cond
    (= parenthesis \() 1
    (= parenthesis \)) -1
    :else 0))

(defn parethesis-sum
  "Calculate sum of parenthesis. ( has value 1 and ) has value -1."
  [input]
  (reduce + (map parenthesis-to-int input)))

(defn calculate-basement-entry
  "Calculate the position of the parenthesis that first turns the sum negative. ( has value 1 and ) has value -1."
  [input]
  (loop [i 0 sum 0]
    (if (< sum 0)
      i
      (recur (inc i) (+ sum (parenthesis-to-int (get input i)))))))

